import sys
from .darwinunit import main


__unittest = True
if sys.argv[0].endswith("__main__.py"):
    # Swizzle argv[0] to make python's unittest.main module happy
    # argv[] = python -m unittest ...<args>
    sys.argv[0] = "unittest"
    sys.argv.insert(0, "-m")
    sys.argv.insert(0, "python")


main(module=None)
